jQuery.fn.blink=function(b){var a={speed:200,blinks:3,startOpacity:0,endOpacity:1,callback:null};var b=jQuery.extend(a,b);return this.each(function(){var c=0;for(i=1;i<=b.blinks;i++){$(this).animate({opacity:b.startOpacity},b.speed).animate({opacity:b.endOpacity},b.speed,function(){c++;if(c==b.blinks&&jQuery.isFunction(b.callback)){b.callback()}})}})};
